import attr
from attr.validators import instance_of

command_string = """




"""

commands = command_string.split()


@attr.s
class Point2D(object):
    x = attr.ib(validator=instance_of(int))
    y = attr.ib(validator=instance_of(int))

    def __add_square__(self, other):  # coords range from 0 to 2
        def add_saturated(i, j):
            return i if i + j > 2 or i + j < 0 else i + j

        return Point2D(
            add_saturated(self.x, other.x),
            add_saturated(self.y, other.y),
        )

    def __add__(self, other):  # coords range from -2 to 2
        candidate = Point2D(self.x + other.x, self.y + other.y)
        return self if abs(candidate.x) + abs(candidate.y) > 2 else candidate


moves = {
    "R": Point2D(1, 0),
    "L": Point2D(-1, 0),
    "U": Point2D(0, 1),
    "D": Point2D(0, -1),
}

pos = Point2D(1, 1)  # start at '5' on a 3x3 grid, starting in the top left
pos = Point2D(-2, 0)  # start at '5' on a 3x3 grid, starting in the center
key_coords = []
for cmd in commands:
    for move in cmd:
        pos = pos + moves[move]
    key_coords.append(pos)

print(key_coords)
